import { Injectable } from '@nestjs/common';
import { InjectRepository } from '@nestjs/typeorm';
import { Repository } from 'typeorm';
import { User } from './entities/user.entity';
import { License } from './entities/license.entity';
import { WebhookEvent } from './entities/webhook-event.entity';

@Injectable()
export class AppsumoWebhookService {
  constructor(
    @InjectRepository(User) private readonly userRepository: Repository<User>,
    @InjectRepository(License) private readonly licenseRepository: Repository<License>,
    @InjectRepository(WebhookEvent) private readonly webhookEventRepository: Repository<WebhookEvent>,
  ) {}

  async handleWebhook(eventData: any): Promise<void> {
    const { event, license_key, plan_id, status, event_timestamp, test, extra } = eventData;

    // Save the webhook event
    await this.saveWebhookEvent(eventData);

    // Process the event based on its type
    switch (event) {
      case 'purchase':
        await this.handlePurchaseEvent(eventData);
        break;
      case 'activate':
        await this.handleActivateEvent(eventData);
        break;
      case 'deactivate':
        await this.handleDeactivateEvent(eventData);
        break;
      case 'upgrade':
        await this.handleUpgradeEvent(eventData);
        break;
      case 'downgrade':
        await this.handleDowngradeEvent(eventData);
        break;
      default:
        console.warn(`Unhandled event type: ${event}`);
    }
  }

  private async saveWebhookEvent(eventData: any): Promise<WebhookEvent> {
    const event = this.webhookEventRepository.create({
      event_type: eventData.event,
      event_data: eventData,
      event_timestamp: new Date(eventData.event_timestamp * 1000),
    });
    return this.webhookEventRepository.save(event);
  }

  private async handlePurchaseEvent(eventData: any): Promise<void> {
    const { license_key, plan_id, event_timestamp } = eventData;
    const license = this.licenseRepository.create({
      license_key,
      plan_id,
      status: 'inactive',
      event_timestamp: new Date(event_timestamp * 1000),
    });
    await this.licenseRepository.save(license);
  }

  private async handleActivateEvent(eventData: any): Promise<void> {
    const { license_key, event_timestamp } = eventData;
    const license = await this.licenseRepository.findOne({ license_key });
    if (license) {
      license.status = 'active';
      license.event_timestamp = new Date(event_timestamp * 1000);
      await this.licenseRepository.save(license);
    }
  }

  private async handleDeactivateEvent(eventData: any): Promise<void> {
    const { license_key, event_timestamp } = eventData;
    const license = await this.licenseRepository.findOne({ license_key });
    if (license) {
      license.status = 'inactive';
      license.event_timestamp = new Date(event_timestamp * 1000);
      await this.licenseRepository.save(license);
    }
  }

  private async handleUpgradeEvent(eventData: any): Promise<void> {
    const { license_key, plan_id, event_timestamp } = eventData;
    const license = await this.licenseRepository.findOne({ license_key });
    if (license) {
      license.plan_id = plan_id;
      license.event_timestamp = new Date(event_timestamp * 1000);
      await this.licenseRepository.save(license);
    }
  }

  private async handleDowngradeEvent(eventData: any): Promise<void> {
    const { license_key, plan_id, event_timestamp } = eventData;
    const license = await this.licenseRepository.findOne({ license_key });
    if (license) {
      license.plan_id = plan_id;
      license.event_timestamp = new Date(event_timestamp * 1000);
      await this.licenseRepository.save(license);
    }
  }
}
