    const keyword = `TIER ${tier}`;
    const products = await this.stripe.products.list();

    const plan = products.data.find(p => !!p.name.toUpperCase().match(keyword));
    if (!plan) {
      throw new Error(`AppSumo Stripe subscription "${keyword}" is missing`);
    }

    const prices = await this.stripe.prices.list({ product: plan.id });

    if (!prices) {
      throw new Error(`AppSumo Stripe subscription "${keyword}" has no prices attached`);
    }

    const price = prices.data.find(price => price.recurring?.usage_type === 'licensed');
    return price;
  }

  private async createStripeSubscription(userId: string, priceId: string) {
    console.log(`Create Stripe Subscription : userId - ${userId}; priceId - ${priceId}`);

    const customerId = await this.stripeService.getCustomerIdByUserId(userId);

    const subscription = await this.stripe.subscriptions.create({
      customer: customerId,
      items: [{ price: priceId }],
    }) as any;

    const stripeProductId = subscription.plan.product as string;

    const planDetails = await this.prisma.subscription.findFirst({
      where: {
        stripeProductId: stripeProductId,
      },
    });

    if (!planDetails) {
      throw new Error(`Could not identify Local Plan for stripe priceId ${priceId}`);
    }

    const subscriptionAlias = planDetails?.alias as string;

    const userSub = await this.prisma.userSubscription.create({
      data: {
        userId,
        subscriptionAlias,
        status: UserSubscriptionStatus.ACTIVE,
        nextBillingAt: null,
        prevBillingAt: null,
        validUntil: new Date(),
        stripeSubscriptionId: subscription.id,
        extraMessages: false,
      },
    });

    console.log('Subscription Created', subscription);
    console.log('userSubscription created', userSub);

    return {
      stripe: subscription,
      userSub: userSub,
    };
  }

  private async createSubscriptionByLicense(licenseId: number, licenseKey: string, userId: string) {
    const licenseData = await this.fetchLicenseByKey(licenseKey);

    if (!licenseData) {
      throw new Error(`AppSumo :: assignLicenseToUser - cannot find License on AppSumo associated with KEY ${licenseKey}`);
    }

    const tier = licenseData.data.tier;

    const sub = await this.subscriptionService.getUserSubscription(userId);
    if (sub?.data.id) {
      await this.subscriptionService.cancelSubscription(userId, true);
    }

    const price = await this.getPriceIdByTier(tier);

    if (!price) {
      throw Error('AppSumo :: assignLicenseToUser - Cannot find price in stripe');
    }

    const subData = await this.createStripeSubscription(userId, price.id);

    const userLicense = await this.prisma.appsumoLicensedUser.create({
      data: {
        licenseId: licenseId,
        userId,
        subscriptionId: subData.userSub.id,
      },
    });

    return userLicense;
  }

  private async assignClaimedLicenseToUser(userId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
    });

    if (!user) {
      throw new Error(`AppSumo :: assignLicenseToUser - cannot find user with ID ${userId}`);
    }

    const sumoLicense = await this.prisma.appsumoLicenseClaimed.findFirst({
      where: { email: user.email },
      include: {
        license: true,
      },
    });

    if (!sumoLicense) {
      throw new Error(`AppSumo :: assignLicenseToUser - cannot find License associated with email ${user.email}`);
    }

    return this.createSubscriptionByLicense(sumoLicense.license.id, sumoLicense.license.licenseKey, userId);
  }

  async assignToUserByCredentials(email: string, password: string) {
    const user = await this.prisma.user.findUnique({
      where: { email },
    });

    if (!user) {
      throw new Error(`AppSumo Service: Could not find user with email ${email} during license assignment`);
    }

    const res = await bcrypt.compare(password, user.passHash);

    if (!res) {
      throw new Error('AppSumo Service: Incorrect password');
    }

    return this.assignClaimedLicenseToUser(user.id);
  }

  @OnEvent(EUserCreated.KEY)
  async onNewUserCreated(event: EUserCreated) {
    const payload = event.payload;
    const { userId } = payload;

    const user = await this.prisma.user.findUnique({
      where: { id: userId },
    });

    const claimFound = await this.prisma.appsumoLicenseClaimed.count({
      where: { email: user?.email },
    });

    if (claimFound > 0) {
      await this.assignClaimedLicenseToUser(userId);
    }
  }
}
