    }
    try {
      const { accessToken } = await this.appSumoService.fetchAccessToken(code);
      const userLicense = await this.appSumoService.fetchUserLicense(accessToken);
      const existingLicense = await this.appSumoService.getPurchasedLicense(userLicense);

      if (!existingLicense) {
        return res.status(401).send('Could not identify License');
      }

      const pathToFile = path.resolve(__dirname, './modules/app-sumo/templates/appsumo.hbs');
      const data = await fs.readFile(pathToFile, 'utf8');

      const template = Handlebars.compile(data);
      const templateData = { licenseId: existingLicense.id };
      const result = template(templateData);

      res.status(200).send(result);
    } catch (error) {
      console.error('Failed to handle redirect', error);
      res.status(500).json({ error: 'Failed to handle redirect' });
    }
  }

  @Post('webhook')
  async handleWebhook(
    @Body() eventData: any,
    @Headers('x-appsumo-signature') signature: string,
    @Headers('x-appsumo-timestamp') timestamp: string,
    @Res() res: Response
  ) {
    try {
      const apiKey = APPSUMO_API_KEY;
      const message = `${timestamp}${JSON.stringify(eventData)}`;
      const hmac = createHmac('sha256', apiKey);
      const generatedSignature = hmac.update(message).digest('hex');

      if (generatedSignature !== signature) {
        throw new Error('Invalid signature');
      }

      await this.hookService.handleWebhook(eventData);
      res.status(200).json({ success: true });
    } catch (error) {
      console.error('Failed to handle webhook', error);
      res.status(500).json({ success: false, error: 'Failed to handle webhook' });
    }
  }

  @Post('webhook-test')
  async handleWebhookTest(@Body() eventData, @Res() res: Response) {
    try {
      await this.hookService.handleWebhook(eventData);
      res.status(200).json({ success: true });
    } catch (error) {
      console.error('Failed to handle webhook', error);
      res.status(500).json({ success: false, error: 'Failed to handle webhook' });
    }
  }

  @Post('claim-existing-user')
  async claimAppSumoUser(@Body() body: ApSumoLoginDto) {
    await this.appSumoService.claimAppSumoLicense(body);
    const { email, password } = body;
    return this.appSumoService.assignToUserByCredentials(email, password);
  }

  @Post('claim-new-user')
  async claimNewAppSumoUser(@Body() body: ApSumoLoginDto) {
    return this.appSumoService.claimAppSumoLicense(body);
  }
}
```

